content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // const { id_departemen } = req.body;
  const id_departemen = "1";
  const jabatan = await db.jabatan.findMany({
    where: {
      id_departemen: parseInt(id_departemen),
    },
  });
  const departemen = await db.departemen.findUnique({
    where: {
      id_departemen: parseInt(id_departemen),
    },
  });
  reply.send({
    jabatan: jabatan,
    departemen: departemen,
  })
}
