content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_departemen } = req.body;
  let jabatan = await db.jabatan.findMany({
    where: {
      id_departemen: id_departemen,
    },
  });
  reply.send({
    jabatan: jabatan,
  })
}
