content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_perizinan } = req.body;

  const dec = await db.perizinan.update({
    where: {
      id_perizinan: parseInt(id_perizinan),
    },
    data: {
      id_pengajuan: 3,
    },
  });

  reply.send({
    status: dec ? "success" : "error",
  })
}
