content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { nama_parameter, pendapatan } = req.body;
  const addParam = await db.parameter_penggajian.create({
    data: {
      nama_parameter: nama_parameter,
      pendapatan: parseInt(pendapatan),
    },
  });
  reply.send({
    status: addParam ? "success" : "error",
  })
}
