content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // Get data from form
  const { nama_pengguna, email, nomor_hp, password, id_jabatan } = req.body;

  // Query
  const add = await db.pengguna.create({
    data: {
      nama_pengguna: nama_pengguna,
      email: email,
      nomor_hp: nomor_hp,
      password: ext.md5(password),
      id_role: 2,
      id_jabatan: id_jabatan,
    },
  });

  if (add) {
    reply.send({
      status: "success",
    });
  } else {
    reply.send({
      status: "error",
    });
  }
}
