content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // Get data from form
  const { nama_pengguna, email, nomor_hp, id_jabatan } = req.body;
  const password = "12345";

  // Query
  // check if email & nomor_hp exist
  const check_email = await db.pengguna.count({
    where: {
      email: email,
    },
  });
  const check_nomor = await db.pengguna.count({
    where: {
      nomor_hp: nomor_hp,
    },
  });
  if (check_email > 0 || check_nomor > 0) {
    reply.send({
      status: check_email > 0 ? "email-exist" : "nomor-exist",
    });
  } else {
    // Add data
    const add = await db.pengguna.create({
      data: {
        nama_pengguna: nama_pengguna,
        email: email,
        nomor_hp: nomor_hp,
        password: ext.md5(password),
        id_role: 2,
        id_jabatan: id_jabatan,
        status_pengguna: "active",
      },
    });

    if (add) {
      reply.send({
        status: "success",
      });
    } else {
      reply.send({
        status: "error",
      });
    }
  }
}
