content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // Get data from form
  const {
    id_pengguna,
    nama_pengguna,
    email,
    nomor_hp,
    id_jabatan,
    id_departemen,
  } = req.body;

  // Query
  const update = await db.pengguna.update({
    where: {
      id_pengguna: parseInt(id_pengguna),
    },
    data: {
      nama_pengguna: nama_pengguna,
      email: email,
      nomor_hp: nomor_hp,
      id_jabatan: parseInt(id_jabatan),
    },
  });

  if (update) {
    reply.send({
      status: "success",
    });
  } else {
    reply.send({
      status: "error",
    });
  }
}
