content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_pengguna, password } = req.body;
  const update = await db.pengguna.update({
    data: {
      password: ext.md5(password),
    },
    where: {
      id_pengguna: id_pengguna,
    },
  });
  reply.send({
    status: update ? true : false,
  })
}
