content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_presensi } = req.body;
  // const id_presensi = 1;
  const presensi = await db.presensi.findFirst({
    where: {
      id_presensi: parseInt(id_presensi),
    },
  });
  const details = await db.karyawan_presensi.findMany({
    where: {
      id_presensi: parseInt(id_presensi),
    },
    include: {
      status_presensi: true,
      presensi: true,
      pengguna: true,
    },
  });
  reply.send({
    presensi: presensi,
    details: details,
  })
}
