content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_pengguna } = req.body;
  const data = await db.pengguna.findFirst({
    where: {
      id_pengguna: parseInt(id_pengguna),
    },
    include: {
      jabatan: {
        include: {
          departemen: true,
        },
      },
    },
  });
  reply.send({
    karyawan: data,
  })
}
