content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { email, password } = req.body;

  const login = await db.pengguna.count({
    where: {
      email: email,
      password: ext.md5(password),
    },
  });

  if (login == 1) {
    const data = await db.pengguna.findFirst({
      where: {
        email: email,
        password: ext.md5(password),
      },
    });
    // Add session
    const firstname = data.nama_pengguna.split(" ");
    const userdata = {
      email: data.email,
      fullname: data.nama_pengguna,
      name: firstname[0],
      role: data.id_role,
      id_pengguna: data.id_pengguna,
      loggedIn: true,
    };
    reply.send({
      status: "success",
      userdata: userdata,
    });
  } else {
    reply.send({
      status: "error",
    });
  }
}
