content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { username, password } = req.body;
  const login = await db.admin.count({
    where: {
      username: username,
      password: password,
    },
  });
  if (login == 1) {
    reply.send({
      status: "success",
    });
  } else {
    reply.send({
      status: "error",
    });
  }
}
