content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { email, password } = req.body;
  const login = await db.pengguna.count({
    where: {
      email: email,
      password: ext.md5(password),
    },
  });
  console.log("user", window.user);
  if (login == 1) {
    const data = await db.pengguna.findFirst({
      where: {
        email: email,
        password: ext.md5(password),
      },
    });
    // Add session

    user.user = {
      email: data.email,
      name: data.nama_pengguna,
      role: data.id_role,
      loggedIn: true,
    };

    reply.send({
      status: "success",
      role: data.id_role,
      // data: user.user,
    });
  } else {
    reply.send({
      status: "error",
    });
  }
}
