content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_jabatan, nama_jabatan } = req.body;
  const up = await db.jabatan.update({
    data: {
      nama_jabatan: nama_jabatan,
    },
    where: {
      id_jabatan: id_jabatan,
    },
  });
  reply.send({
    hello: up ? "success" : "error",
  })
}
