content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // Get id
  const { id_pengguna } = req.body;

  // Query
  const del = await db.pengguna.delete({
    where: {
      id_pengguna: id_pengguna,
    },
  });

  if (del) {
    reply.send({
      status: "success",
    });
  } else {
    reply.send({
      status: "error",
    });
  }
}
