content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let month, year;
  if (!req.body) {
    let date = new Date();
    month = date.getMonth() + 1;
    year = date.getFullYear();
  } else {
    // let { month, year } = req.body;
    month = req.body.month;
    year = req.body.year;
  }

  // penggajian
  const penggajian = await db.penggajian.findMany({
    where: {
      bulan: month.toString(),
      tahun: year.toString(),
    },
    include: {
      pengguna: {
        include: {
          jabatan: {
            include: {
              departemen: true,
            },
          },
        },
      },
    },
    orderBy: {
      id_penggajian: "desc",
    },
  });
  const penggajian_bulan = await db.penggajian.groupBy({
    by: ["bulan"],
    _count: {
      bulan: true,
    },
    orderBy: {
      bulan: "asc",
    },
  });
  const penggajian_tahun = await db.penggajian.groupBy({
    by: ["tahun"],
    _count: {
      tahun: true,
    },
    orderBy: {
      tahun: "asc",
    },
  });
  penggajian_bulan.map((val, index) => {
    if (parseInt(val.bulan) == 1) {
      val.nama_bulan = "Januari";
    } else if (parseInt(val.bulan) == 2) {
      val.nama_bulan = "Februari";
    } else if (parseInt(val.bulan) == 3) {
      val.nama_bulan = "Maret";
    } else if (parseInt(val.bulan) == 4) {
      val.nama_bulan = "April";
    } else if (parseInt(val.bulan) == 5) {
      val.nama_bulan = "Mei";
    } else if (parseInt(val.bulan) == 6) {
      val.nama_bulan = "Juni";
    } else if (parseInt(val.bulan) == 7) {
      val.nama_bulan = "Juli";
    } else if (parseInt(val.bulan) == 8) {
      val.nama_bulan = "Agustus";
    } else if (parseInt(val.bulan) == 9) {
      val.nama_bulan = "September";
    } else if (parseInt(val.bulan) == 10) {
      val.nama_bulan = "Oktober";
    } else if (parseInt(val.bulan) == 11) {
      val.nama_bulan = "November";
    } else if (parseInt(val.bulan) == 12) {
      val.nama_bulan = "Desember";
    }
  });

  reply.send({
    penggajian: penggajian,
    penggajian_bulan: penggajian_bulan,
    penggajian_tahun: penggajian_tahun,
    month: month,
    year: year,
  })
}
