content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { date, limit, token } = req.body;
  date = date.split("-");
  const year = date[0];
  const month = date[1].slice(0, 1) == "0" ? date[1].slice(1, 2) : date[1];
  const day = date[2];
  const create_attendance = await db.presensi.create({
    data: {
      hari: day,
      bulan: month,
      tahun: year,
      token: token,
      limit: limit,
    },
  });
  // Membuat presensi karyawan dengan default tidak hadir
  if (create_attendance) {
    // Mengambil semua karyawan
    const karyawan = await db.pengguna.findMany({
      where: {
        id_role: 2,
      },
    });

    // Mendapatkan id_presensi
    const id_presensi = await db.presensi.findFirst({
      where: {
        hari: day,
        bulan: month,
        tahun: year,
        token: token,
        limit: limit,
      },
    });

    for (let i = 0; i < karyawan.length; i++) {
      await db.karyawan_presensi.create({
        data: {
          id_presensi: id_presensi.id_presensi,
          id_karyawan: karyawan[i].id_pengguna,
          id_status: 2,
        },
      });
    }
  }
  reply.send({
    status: create_attendance ? "success" : "error",
  })
}
