content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const date = new Date();
  const data = await db.penggajian.findMany({
    where: {
      bulan: (date.getMonth() + 1).toString(),
      tahun: date.getFullYear().toString(),
    },
    include: {
      pengguna: {
        include: {
          jabatan: {
            include: {
              departemen: true,
            },
          },
        },
      },
    },
  });

  // Cek apakah data pada bulan ini sudah ada atau belum
  const karyawan = await db.pengguna.findMany({
    where: {
      id_role: 2,
      status_pengguna: "active",
    },
  });

  // if(karyawan.length >= data.length)

  let total = 0;
  for (let i = 0; i < data.length; i++) {
    total += data[i].nominal;
  }
  reply.send({
    penggajian: data,
    total: total,
  })
}
