content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_penggajian } = req.body;

  // Presensi bulan ini
  let year;
  const {} = req.body;
  // const id_pengguna.id_pengguna = 46;
  const date = new Date();
  let year_now, month_now;
  month_now = (date.getMonth() + 1).toString();
  if (!req.body.year) {
    year = date.getFullYear().toString();
    year_now = year;
  } else {
    year = req.body.year;
    year_now = year;
  }
  // Presensi bulan ini
  const id_pengguna = await db.penggajian.findFirst({
    where: {
      id_penggajian: parseInt(id_penggajian),
    },
  });
  const pengguna = await db.pengguna.findFirst({
    where: {
      id_pengguna: id_pengguna.id_pengguna,
    },
  });
  const presensi_karyawan = await db.karyawan_presensi.findMany({
    where: {
      id_karyawan: id_pengguna.id_pengguna,
    },
    include: {
      presensi: true,
    },
  });

  // Filter bulan ini
  let presensi_bulan_ini = [];
  presensi_karyawan.map((val) => {
    if (val.presensi.bulan == month_now && val.presensi.tahun == year_now) {
      presensi_bulan_ini.push(val);
    }
  });
  // Count data
  let hadir = 0;
  let tidak_hadir = 0;
  let izin = 0;
  let sakit = 0;
  let cuti = 0;
  presensi_bulan_ini.map((val) => {
    if (val.id_status == 1) {
      hadir += 1;
    } else if (val.id_status == 2) {
      tidak_hadir += 1;
    } else if (val.id_status == 3) {
      sakit += 1;
    } else if (val.id_status == 4) {
      izin += 1;
    } else if (val.id_status == 5) {
      cuti += 1;
    }
  });

  // Detail gaji
  const detail = await db.detail_penggajian.findMany({
    where: {
      id_penggajian: parseInt(id_penggajian),
    },
  });
  let gaji_pokok, tambahan, potongan;
  for (let i = 0; i < detail.length; i++) {
    if (detail[i].jenis == "pokok") {
      gaji_pokok = detail[i];
    } else if (detail[i].jenis == "bonus") {
      tambahan = detail[i];
    } else if (detail[i].jenis == "potongan") {
      potongan = detail[i];
    }
  }
  reply.send({
    gaji_pokok,
    tambahan,
    potongan,
    pengguna,
    hadir,
    tidak_hadir,
    izin,
    cuti,
    sakit,
  })
}
