content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_departemen, nama_jabatan } = req.body;
  const add = await db.jabatan.create({
    data: {
      nama_jabatan: nama_jabatan,
      id_departemen: parseInt(id_departemen),
    },
  });
  reply.send({
    status: add ? "success" : "error",
  })
}
