content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_parameter } = req.body;
  // const id_parameter = 9;
  // Get data
  const getKaryawan = await db.pengguna.findMany({
    where: {
      id_role: 2,
    },
    include: {
      jabatan: {
        include: {
          departemen: true,
        },
      },
    },
  });
  const getParameter = await db.karyawan_parameter_penggajian.findMany({
    where: {
      id_parameter: parseInt(id_parameter),
    },
  });

  // Merge data
  for (let i = 0; i < getKaryawan.length; i++) {
    getKaryawan[i].checked = false;
  }

  for (let i = 0; i < getKaryawan.length; i++) {
    for (let j = 0; j < getParameter.length; j++) {
      if (getKaryawan[i].id_pengguna == getParameter[j].id_karyawan) {
        getKaryawan[i].checked = true;
      }
    }
  }
  reply.send({
    karyawan: getKaryawan,
  })
}
