content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // Cek status penggajian awal
  const penggajian = await db.penggajian.findFirst({
    where: {
      id_status: 1,
    },
  });
  // Kirim
  const kirim = await db.$queryRaw(
    "update penggajian set id_status=2 where bulan='" +
      penggajian.bulan +
      "' and tahun='" +
      penggajian.tahun +
      "'"
  );
  reply.send({
    status: kirim,
  })
}
