content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_pengguna } = req.body;

  const presensi = await db.presensi.findMany({
    include: {
      karyawan_presensi: {
        where: {
          id_karyawan: id_pengguna,
        },
        include: {
          status_presensi: true,
        },
      },
    },
  });
  reply.send({
    presensi: presensi,
  })
}
