content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let month, year;
  if (!req.body) {
    let date = new Date();
    month = date.getMonth() + 1;
    year = date.getFullYear();
  } else {
    // let { month, year } = req.body;
    month = req.body.month;
    year = req.body.year;
  }

  // akumulasi presensi harian
  // Total karyawan aktif
  const total_karyawan = await db.pengguna.count({
    where: {
      id_role: 2,
      status_pengguna: "active",
    },
  });
  // Hadir, sakit izin dkk ?

  // Perizinan
  const perizinan = await db.$queryRaw(
    "select perizinan.*, pengguna.nama_pengguna, status_pengajuan.pengajuan from perizinan inner join pengguna on pengguna.id_pengguna=perizinan.id_pengguna inner join status_pengajuan on status_pengajuan.id_pengajuan=perizinan.id_pengajuan where extract(year from tgl_izin)='" +
      year +
      "' and extract(month from tgl_izin)='" +
      month +
      "'"
  );
  const perizinan_bulan = await db.$queryRaw`select extract(month from tgl_izin) as bulan from perizinan group by bulan`;
  const perizinan_tahun = await db.$queryRaw`select extract(year from tgl_izin) as tahun from perizinan group by tahun`;
  perizinan_bulan.map((val, index) => {
    if (parseInt(val.bulan) == 1) {
      val.nama_bulan = "Januari";
    } else if (parseInt(val.bulan) == 2) {
      val.nama_bulan = "Februari";
    } else if (parseInt(val.bulan) == 3) {
      val.nama_bulan = "Maret";
    } else if (parseInt(val.bulan) == 4) {
      val.nama_bulan = "April";
    } else if (parseInt(val.bulan) == 5) {
      val.nama_bulan = "Mei";
    } else if (parseInt(val.bulan) == 6) {
      val.nama_bulan = "Juni";
    } else if (parseInt(val.bulan) == 7) {
      val.nama_bulan = "Juli";
    } else if (parseInt(val.bulan) == 8) {
      val.nama_bulan = "Agustus";
    } else if (parseInt(val.bulan) == 9) {
      val.nama_bulan = "September";
    } else if (parseInt(val.bulan) == 10) {
      val.nama_bulan = "Oktober";
    } else if (parseInt(val.bulan) == 11) {
      val.nama_bulan = "November";
    } else if (parseInt(val.bulan) == 12) {
      val.nama_bulan = "Desember";
    }
  });

  reply.send({
    total_karyawan: total_karyawan,
    perizinan: perizinan,
    perizinan_bulan: perizinan_bulan,
    perizinan_tahun: perizinan_tahun,
    month: month,
    year: year,
  })
}
