content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_parameter, prev_checked } = req.body;
  const new_checked = !prev_checked;
  const check = await db.parameter_penggajian.update({
    data: {
      default: new_checked,
    },
    where: {
      id_parameter: parseInt(id_parameter),
    },
  });
  reply.send({
    status: check ? "success" : "error",
  })
}
