content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_jabatan } = req.body;
  const r_jabatan = await db.jabatan.delete({
    where: {
      id_jabatan: id_jabatan,
    },
  });
  reply.send({
    status: r_jabatan ? "success" : "error",
  })
}
