content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_pengguna } = req.body;
  const data = await db.pengguna.findUnique({
    where: {
      id_pengguna: parseInt(id_pengguna),
    },
    include: {
      jabatan: {
        include: {
          departemen: true,
        },
      },
    },
  });
  const parameter_karyawan = await db.karyawan_parameter_penggajian.findMany({
    where: {
      id_karyawan: parseInt(id_pengguna),
    },
    include: {
      parameter_penggajian: true,
    },
  });
  const parameter = await db.parameter_penggajian.findMany();

  for (let i = 0; i < parameter.length; i++) {
    parameter[i].checked = false;
  }
  for (let i = 0; i < parameter.length; i++) {
    for (let j = 0; j < parameter_karyawan.length; j++) {
      if (parameter[i].id_parameter == parameter_karyawan[j].id_parameter) {
        parameter[i].checked = true;
      }
    }
  }
  reply.send({
    userdata: data,
    parameter_karyawan: parameter_karyawan,
    parameter: parameter,
  })
}
