content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_pengguna, password } = req.body;
  const check = await db.pengguna.count({
    where: {
      id_pengguna: parseInt(id_pengguna),
      password: ext.md5(password),
    },
  });
  reply.send({
    status: check > 0 ? true : false,
  })
}
