content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const users = await db.pengguna.findMany({
    where: {
      id_role: 2,
    },
    include: {
      departemen: {
        select: {
          nama_departemen: true,
        },
      },
    },
  });
  reply.send({
    karyawan: users,
  })
}
