content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_departemen, nama_departemen } = req.body;
  const up = await db.departemen.update({
    data: {
      nama_departemen: nama_departemen,
    },
    where: {
      id_departemen: id_departemen,
    },
  });
  reply.send({
    status: up ? "success" : "error",
  })
}
