content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_detail } = req.body;
  const detail = await db.detail_penggajian.findFirst({
    where: {
      id_detail: parseInt(id_detail),
    },
  });
  reply.send({
    detail: detail,
  })
}
