content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // Presensi Hari ini
  let day_today, month_today, year_today;
  let date = new Date();
  day_today = date.getDate().toString();
  month_today = (date.getMonth() + 1).toString();
  year_today = date.getFullYear().toString();

  // akumulasi perizinan harian
  // Total karyawan aktif
  const total_karyawan = await db.pengguna.count({
    where: {
      id_role: 2,
      status_pengguna: "active",
    },
  });

  // sakit izin dkk
  // Presensi hari ini
  const presensi_today = await db.presensi.findFirst({
    where: {
      hari: day_today,
      bulan: month_today,
      tahun: year_today,
    },
    orderBy: {
      id_presensi: "desc",
    },
  });

  const detail_presensi_today = await db.karyawan_presensi.findMany({
    where: {
      id_presensi: presensi_today.id_presensi,
    },
    include: {
      status_presensi: true,
    },
  });
  let hadir = 0;
  let sakit = 0;
  let tidak_hadir = 0;
  let izin = 0;
  let cuti = 0;
  detail_presensi_today.map((val) => {
    if (val.id_status == 1) {
      hadir += 1;
    } else if (val.id_status == 2) {
      tidak_hadir += 1;
    } else if (val.id_status == 3) {
      sakit += 1;
    } else if (val.id_status == 4) {
      izin += 1;
    } else if (val.id_status == 5) {
      cuti += 1;
    }
  });

  // Transfer terakhir
  const transaksi = await db.penggajian.findMany({
    where: {
      id_status: 2,
    },
    include: {
      status_penggajian: true,
    },
    orderBy: {
      id_penggajian: "desc",
    },
  });
  let transaksi_terakhir = [];

  if (transaksi.length > 2) {
    for (let i = 0; i < 2; i++) {
      transaksi_terakhir.push(transaksi[i]);
    }
  } else {
    transaksi_terakhir = transaksi;
  }

  reply.send({
    total_karyawan,
    hadir,
    sakit,
    tidak_hadir,
    izin,
    cuti,
    transaksi_terakhir,
  })
}
