content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { nama_departemen } = req.body;

  // Add to db
  const add = await db.departemen.create({
    data: {
      nama_departemen: nama_departemen,
    },
  });

  if (add) {
    // Get id_departemen
    const id_departemen = await db.departemen.findFirst({
      select: {
        id_departemen: true,
      },
      where: {
        nama_departemen: nama_departemen,
      },
      orderBy: {
        id_departemen: "desc",
      },
    });

    reply.send({
      status: "success",
      id_departemen: id_departemen,
    });
  } else {
    reply.send({
      status: "error",
    });
  }
}
