content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { nama_departemen } = req.body;
  const add = await db.departemen.create({
    data: {
      nama_departemen: nama_departemen,
    },
  });
  reply.send({
    status: add ? "success" : "error",
  })
}
