async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_perizinan } = req.body;
  // const detail = await db.perizinan.findFirst({
  //   where: {
  //     id_perizinan: id_perizinan,
  //   },
  //   include: {
  //     pengguna: true,
  //     status_pengajuan: true,
  //   },
  // });
  const detail = await db.$queryRaw(
    "select * from perizinan inner join pengguna on pengguna.id_pengguna=perizinan.id_pengguna inner join status_pengajuan on perizinan.id_pengajuan=status_pengajuan.id_pengajuan where id_perizinan=" +
      id_perizinan
  );
  reply.send({
    perizinan: detail,
  })
}
