content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_pengguna } = req.body;
  const pengguna = await db.pengguna.findUnique({
    where: {
      id_pengguna: id_pengguna,
    },
  });
  const status_pengguna =
    pengguna.status_pengguna == "active" ? "inactive" : "active";

  const toggle = await db.pengguna.update({
    where: {
      id_pengguna: id_pengguna,
    },
    data: {
      status_pengguna: status_pengguna,
    },
  });
  reply.send({
    status: toggle ? "success" : "error",
  })
}
