content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let year;
  const { id_pengguna } = req.body;
  // const id_pengguna = 46;
  const date = new Date();
  let year_now, month_now;
  month_now = (date.getMonth() + 1).toString();
  if (!req.body.year) {
    year = date.getFullYear().toString();
    year_now = year;
  } else {
    year = req.body.year;
    year_now = year;
  }
  // Presensi bulan ini
  const presensi_karyawan = await db.karyawan_presensi.findMany({
    where: {
      id_karyawan: id_pengguna,
    },
    include: {
      presensi: true,
    },
  });

  // Filter bulan ini
  let presensi_bulan_ini = [];
  presensi_karyawan.map((val) => {
    if (val.presensi.bulan == month_now && val.presensi.tahun == year_now) {
      presensi_bulan_ini.push(val);
    }
  });
  // Count data
  let hadir = 0;
  let tidak_hadir = 0;
  let izin = 0;
  let sakit = 0;
  let cuti = 0;
  presensi_bulan_ini.map((val) => {
    if (val.id_status == 1) {
      hadir += 1;
    } else if (val.id_status == 2) {
      tidak_hadir += 1;
    } else if (val.id_status == 3) {
      sakit += 1;
    } else if (val.id_status == 4) {
      izin += 1;
    } else if (val.id_status == 5) {
      cuti += 1;
    }
  });

  // Gaji
  const gaji = await db.penggajian.findMany({
    where: {
      id_pengguna: id_pengguna,
      id_status: 2,
      tahun: year,
    },
    orderBy: {
      id_penggajian: "desc",
    },
  });
  const penggajian_tahun = await db.penggajian.groupBy({
    by: ["tahun"],
    _count: {
      tahun: true,
    },
    where: {
      id_pengguna: id_pengguna,
      id_status: 2,
      tahun: year,
    },
    orderBy: {
      tahun: "asc",
    },
  });
  gaji.map((val, index) => {
    if (parseInt(val.bulan) == 1) {
      val.nama_bulan = "Januari";
    } else if (parseInt(val.bulan) == 2) {
      val.nama_bulan = "Februari";
    } else if (parseInt(val.bulan) == 3) {
      val.nama_bulan = "Maret";
    } else if (parseInt(val.bulan) == 4) {
      val.nama_bulan = "April";
    } else if (parseInt(val.bulan) == 5) {
      val.nama_bulan = "Mei";
    } else if (parseInt(val.bulan) == 6) {
      val.nama_bulan = "Juni";
    } else if (parseInt(val.bulan) == 7) {
      val.nama_bulan = "Juli";
    } else if (parseInt(val.bulan) == 8) {
      val.nama_bulan = "Agustus";
    } else if (parseInt(val.bulan) == 9) {
      val.nama_bulan = "September";
    } else if (parseInt(val.bulan) == 10) {
      val.nama_bulan = "Oktober";
    } else if (parseInt(val.bulan) == 11) {
      val.nama_bulan = "November";
    } else if (parseInt(val.bulan) == 12) {
      val.nama_bulan = "Desember";
    }
  });

  reply.send({
    gaji: gaji,
    years: penggajian_tahun,
    year_now: year_now,
    hadir: hadir,
    tidak_hadir: tidak_hadir,
    sakit: sakit,
    cuti: cuti,
    izin: izin,
  })
}
