content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_departemen } = req.body;
  // Cek jika departemen memiliki jabatan
  const check = await db.jabatan.count({
    where: {
      id_departemen: id_departemen,
    },
  });

  if (check > 0) {
    reply.send({
      status: "error-occure",
    });
  } else {
    // delete departemen
    const del = await db.departemen.delete({
      where: {
        id_departemen: id_departemen,
      },
    });
    reply.send({
      status: del ? "success" : "error",
    });
  }
}
